ProcessingScript.script:
var ExecutionUtil = Java.type("com.boomi.execution.ExecutionUtil");
var Thread = Java.type("java.lang.Thread");

var dppSleepMilliseconds = ExecutionUtil.getDynamicProcessProperty('DPP_SLEEP_MILLISECONDS');

try {
    sleepTime = parseInt(dppSleepMilliseconds);
    if (isNaN(sleepTime)) {
        sleepTime = 100;
    }
} catch (ex) {
    sleepTime = 100;
}
try {
    Thread.sleep(sleepTime);
} catch (ex) { }

for (var i = 0; i < dataContext.getDataCount(); i++ ) {
    var is = dataContext.getStream(i);
    var props = dataContext.getProperties(i);

    dataContext.storeStream(is, props);
}
